import React from 'react';
import Header from './Header';
import Footer from './Footer';
import NeonBg from './images/NeonBg.jpg'; // Assuming NeonBg.jpg is in the images folder

function Homepage() {
  return (
    <div className="h-screen bg-cover bg-no-repeat bg-center"> {/* Background image styles */}
      <div className="flex flex-col h-full"> {/* Content container with flexbox */}
        <Header />
        <div className="flex-grow p-7 place-content-center"> {/* Main content with flex-grow */}
          <h1 className="text-4xl">Chhon</h1>
          <h1 className="text-7xl">KimEag</h1>
          <p className="text-3xl">OW Student</p>
        </div>
        <Footer />
      </div>
    </div>
  );
}

export default Homepage;
